hudson.tasks.Shell.command:
#!/bin/sh
ntpdate ntp.hp.net;
cat /etc/issue |grep -iE 'debian|ubuntu';
if [ $? -eq 0 ] ; then
  n=`date |awk '{print $3}'`;
  a=$(($n%2));
  if [ $a -eq 0 ] ; then
    sed 's/fc/fc/' /etc/apt/apt.conf > /etc/apt/apt.conf.new;
  else
    sed 's/fc/atl/' /etc/apt/apt.conf > /etc/apt/apt.conf.new;
  fi
  cp /etc/apt/apt.conf.new /etc/apt/apt.conf;
  apt-get clean;
  apt-get update;
  apt-get -y install git php5 phpunit;
else
  cat /etc/issue |grep -iE 'fedora';
  if [ $? -eq 0 ] ; then
    sed 's/#proxy=/proxy=/' /etc/yum.conf > /etc/yum.conf.new;
    cp /etc/yum.conf.new /etc/yum.conf;
    #echo 'proxy=http://web-proxy.fc.hp.com:8088/' >> /etc/yum.conf;
    #sed 's/baseurl=/#baseurl=/' /etc/yum.repos.d/fedora.repo > /etc/yum.repos.d/fedora.repo.new
    #sed 's/##baseurl=/baseurl=/' /etc/yum.repos.d/fedora.repo.new > /etc/yum.repos.d/fedora.repo.new1
    #cp /etc/yum.repos.d/fedora.repo.new1 /etc/yum.repos.d/fedora.repo;
    #sed 's/baseurl=/#baseurl=/' /etc/yum.repos.d/fedora-updates.repo > /etc/yum.repos.d/fedora-updates.repo.new
    #sed 's/##baseurl=/baseurl=/' /etc/yum.repos.d/fedora-updates.repo.new > /etc/yum.repos.d/fedora-updates.repo.new1
    #cp /etc/yum.repos.d/fedora-updates.repo.new1 /etc/yum.repos.d/fedora-updates.repo;
    sed 's/fedora/debian/' /etc/yum.repos.d/fedora.repo > /etc/yum.repos.d/fedora.repo.new
    cp /etc/yum.repos.d/fedora.repo.new /etc/yum.repos.d/fedora.repo;
    sed 's/fedora/debian/' /etc/yum.repos.d/fedora-updates.repo > /etc/yum.repos.d/fedora-updates.repo.new
    cp /etc/yum.repos.d/fedora-updates.repo.new /etc/yum.repos.d/fedora-updates.repo;
    yum clean all;
    yum -y update;
    yum -y install git php php-process php-phpunit-PHPUnit;
    yum -y update;
    yum -y update;
    yum -y install git php php-process php-phpunit-PHPUnit;
    setenforce 0;
  else
    sed 's/fc/cce/' /etc/yum.conf > /etc/yum.conf.new;
    cp /etc/yum.conf.new /etc/yum.conf;
    sed 's/centos/debian/' /etc/yum.repos.d/CentOS-Base.repo > /etc/yum.repos.d/CentOS-Base.repo.new;
    cp /etc/yum.repos.d/CentOS-Base.repo.new /etc/yum.repos.d/CentOS-Base.repo
    yum clean all;
    yum -y install lsb wget git php php-process;
    setenforce 0;
    wget -e http_proxy=http://web-proxy.cce.hp.com:8088 http://dl.fedoraproject.org/pub/epel/6/i386/epel-release-6-8.noarch.rpm;
    rpm -ivh epel-release-6-8.noarch.rpm;
    sed 's/#baseurl=/baseurl=/' /etc/yum.repos.d/epel.repo > /etc/yum.repos.d/epel.repo.new
    sed 's/mirrorlist=/#mirrorlist=/' /etc/yum.repos.d/epel.repo.new > /etc/yum.repos.d/epel.repo.new1
    cp /etc/yum.repos.d/epel.repo.new1 /etc/yum.repos.d/epel.repo
    yum -y install php-phpunit-PHPUnit;
    yum -y install php-phpunit-PHPUnit;
    yum -y install php-phpunit-PHPUnit;
  fi
fi
#svn info;
#echo http-proxy-host = web-proxy.cce.hp.com >> ~/.subversion/servers;
#echo http-proxy-port = 8088 >> ~/.subversion/servers;
#svn co https://svn.code.sf.net/p/fossology/code/trunk/fossology;
git config --global http.proxy "http://web-proxy.cce.hp.com:8088";
git config --global https.proxy "http://web-proxy.cce.hp.com:8088";
git clone --depth 1 https://github.com/fossology/fossology.git fossology;
pwd;
export FOSSOLOGY_TESTCONFIG=/etc/fossology/
cd ./fossology/src/testing/install/;
./pkgConfig.php;
cp -r test4packageinstall/ /srv/fossology/;
cat /etc/issue |grep -iE 'ubuntu 12.04';
if [ $? -eq 0 ] ; then
  export http_proxy=http://web-proxy.cce.hp.com:8088/
  cp ../../composer.json /srv/fossology/test4packageinstall;
  cd /srv/fossology/test4packageinstall;
  curl -sS --proxy http://web-proxy.cce.hp.com:8088 https://getcomposer.org/installer | php && sudo mv composer.phar /usr/local/bin/composer;
  composer install;
  ./vendor/phpunit/phpunit/phpunit.php test_cp2foss.php
else
  cd /srv/fossology/test4packageinstall;
  phpunit test_cp2foss.php
fi
